    //     value={formProps.input.value} />
    // }

        renderInput({ input }){
            console.log({input});
            return <input {...input} />
        }

    render() {
        return (
        <form>
            <Field name="title" component={this.renderInput} />
            <Field name="description" component={this.renderInput} />
        </form>
        )
    }
}


export default reduxForm({
    form: 'streamCreate'
})(StreamCreate);
